import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../Cards.css';
import Table from '@material-ui/core/Table';
import TableRow from '@material-ui/core/TableRow';
import TableHead from '@material-ui/core/TableHead';
import TableCell from '@material-ui/core/TableCell';
import TableBody from '@material-ui/core/TableBody';


function Today() {
    const [users, setUsers] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const sites = [
        {
            s: "https://www.naver.com",
        },
        {
            s: "https://www.daum.com"
        }
    ]

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                // 요청이 시작 할 때에는 error 와 users 를 초기화하고
                setError(null);
                setUsers(null);
                // loading 상태를 true 로 바꿈
                setLoading(true);
                const response = await axios.get(
                    'http://nanunyeogi.paas-ta.org/covid'
                );
                setUsers(response.data.item); // 데이터는 response.data 안에^^
                console.log(users);
            } catch (e) {
                setError(e);
            }
            setLoading(false);
        };

        fetchUsers();
    }, []);

    if (loading) return <div>로딩중..</div>;
    if (error) return <div>에러가 발생했습니다</div>;
    if (!users) return null;
    return (
        <>
            <div className='cards'>
                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>
                            <Table celled striped >
                                <TableHead>
                                    <TableRow>
                                        <TableCell width='100'>지역</TableCell>
                                        <TableCell width='150'>총확진자 수</TableCell>
                                        <TableCell width='150'>신규확진자 수</TableCell>
                                        <TableCell width='150'>사이트</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {users.map(user => (
                                        <TableRow>
                                            <TableCell>{user.gubun}</TableCell>
                                            <TableCell>{user.defCnt}</TableCell>
                                            <TableCell>{user.incDec}</TableCell>
                                            <TableCell><a target="_blank" href={sites.s}>{user.gubun}홈페이지</a></TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </ul>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Today;

