import React from 'react';
import axios from 'axios';



class Today extends React.Component {
    state = {
        data: [],
    };

    handleChange = (e) => {
        const {name, value} = e.target;
        this.setState({
            [name] : value,
        });
    };

    loadingData = async () => {
        try {
            const response = await axios.get('http://nanunyeogi.paas-ta.org/covid',{
                headers: {
                   'Access-Control-Allow-Origin' : '*'
                }
            })         
            this.setState({
                data : response.data,
            });
        } catch(e) {
            console.log(e);
        }
    };
    componentDidMount() {
        const {loadingData} = this;
        loadingData();
    }
    render() {
        const {data} = this.state;
        const {handleChange} = this;
        return (
            <div>
                <h1>바보</h1>
            </div>
        )
    }
}

export default Today;

